and status >=5
and limit_days = #{termType,jdbcType=INTEGER}
ORDER BY
if((STATUS=2 and (total_amount-invested_amount)<100),3,STATUS) ASC,
if((STATUS=2 and (total_amount-invested_amount)>=100),start_time,null) ASC,
if((STATUS=2 and (total_amount-invested_amount)>=100),null,deadline) desc
LIMIT #{limitStart,jdbcType=INTEGER},#{limitEnd,jdbcType=INTEGER}

SELECT count(*) FROM project p LEFT JOIN project pp ON p.parent_id = pp.id WHERE
p.parent_id IS NOT NULL AND p.parent_id > 506 AND ((p.project_type=1) or (p.project_type=7 and p.is_show_at_bond_invest=1)) AND p.`status`!=2
and p.`status`= 0 and p.total_amount - p.invested_amount >=100
and (p.`status`= 1 or (p.`status`= 0 and p.total_amount - p.invested_amount < 100))
and CASE WHEN p.status= 0 THEN DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY),NOW()) ELSE DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY), p.create_time) END > #{limitDaysStart,jdbcType=INTEGER}
and CASE WHEN p.status= 0 THEN DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY),NOW()) ELSE DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY), p.create_time) END <= #{limitDaysEnd,jdbcType=INTEGER}

SELECT p.*, pp.title AS trueTitle, round((pp.annualized - pp.increase_annualized)*100,1) as trueAnnualized, round(pp.increase_annualized*100,1) as addRate, round(p.total_amount-p.invested_amount,2) as voteAmount, case when p.total_amount-p.invested_amount > 0 then round(((p.total_amount-p.invested_amount)*(pp.annualized - pp.increase_annualized)*DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY),NOW()))/365,2) else round(((p.total_amount)*(pp.annualized - pp.increase_annualized)*DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY), p.create_time))/365,2) end as expectedProfit,
CASE WHEN p.status= 0 THEN DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY),NOW()) ELSE DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY), p.create_time) END as dayDiff, pp.limit_days as trueLimitDays, p.bond_management_rate bondManagementRate,
case when p.total_amount-p.invested_amount > 0 then ((round(((p.total_amount-p.invested_amount)*(pp.annualized - pp.increase_annualized)*DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY),NOW()))/365,2)) + p.bond_management_rate * (p.total_amount-p.invested_amount)) / (p.total_amount-p.invested_amount) / (DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY),NOW())) * 365 * 100 else ((round(((p.total_amount)*(pp.annualized - pp.increase_annualized)*DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY), p.create_time))/365,2)) + p.bond_management_rate * p.total_amount) / p.total_amount / (DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY), p.create_time)) * 365 * 100 end as orderbyRate FROM project p LEFT JOIN project pp ON p.parent_id = pp.id WHERE
p.parent_id IS NOT NULL AND p.parent_id > 506 AND ((p.project_type=1) or (p.project_type=7 and p.is_show_at_bond_invest=1)) AND p.`status`!=2
and p.`status`= 0 and p.total_amount - p.invested_amount >=100
and (p.`status`= 1 or (p.`status`= 0 and p.total_amount - p.invested_amount < 100))
and CASE WHEN p.status= 0 THEN DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY),NOW()) ELSE DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY), p.create_time) END > #{limitDaysStart,jdbcType=INTEGER}
and CASE WHEN p.status= 0 THEN DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY),NOW()) ELSE DATEDIFF(ADDDATE(pp.deadline, INTERVAL pp.limit_days DAY), p.create_time) END <= #{limitDaysEnd,jdbcType=INTEGER}
order by p.status asc,
if(p.status=0,orderbyRate,0) desc,
if(p.status=0,dayDiff,0) asc,
if(p.status=0,p.create_time,null) asc,
if(p.status=0,null,p.create_time) desc
LIMIT #{limitStart,jdbcType=INTEGER},#{limitEnd,jdbcType=INTEGER}

