select "0-6" as ageDesc, count(1) as ageQuantity from project_property_view ppv ,project p1 where 1=1 and ppv.id=p1.id and ppv.yue_ling between 0 and 6 and ppv.id in (select p.id from project p where p.product_id in (select a.id from product a where a.`name` like '%%'))
and p1.prairie_value = #{prairieValue}
union all select "6-12" as ageDesc, count(1) as ageQuantity from project_property_view ppv ,project p1 where 1=1 and ppv.id=p1.id and ppv.yue_ling between 6 and 12 and ppv.id in (select p.id from project p where p.product_id in (select a.id from product a where a.`name` like '%%'))
and p1.prairie_value = #{prairieValue}
union all select "12-24" as ageDesc , count(1) as ageQuantity from project_property_view ppv ,project p1 where 1=1 and ppv.id=p1.id and ppv.yue_ling between 12 and 24 and ppv.id in (select p.id from project p where p.product_id in (select a.id from product a where a.`name` like '%%'))
and p1.prairie_value = #{prairieValue}
union all select "24" as ageDesc , count(1) as ageQuantity from project_property_view ppv ,project p1 where 1=1 and ppv.id=p1.id and ppv.yue_ling >24 and ppv.id in (select p.id from project p where p.product_id in (select a.id from product a where a.`name` like '%%'))
and p1.prairie_value = #{prairieValue}

select u.true_name,u.phone,i.amount, date_format(i.create_date, '%Y-%m-%d %H:%i:%s') as deal_time,i.client ,od.create_date as buy_back, p.limit_days, p.weight ,ppv.yue_ling ,p.raise_fee,p.manage_fee ,0 as service_free ,'' as status from project p inner join investment i on p.id=i.project_id inner join user u on i.user_id=u.id inner join order_done od on od.order_no=i.order_no inner join project_property_view ppv on ppv.id=p.id where i.pay_status=2 and p.ear_number=#{earNumber} and p.status=4 and od.order_type='investment' and od.order_status='success'
limit #{start},
#{limit}

select p.id, p.ear_number, i.order_no, i.create_date, (select count(1) from project_life_picture where ear_number = p.ear_number and status = 1) as pic_count, (select count(1) from project_life_picture where ear_number = p.ear_number and status = 1 and is_read = 0) as new_count from project p inner join investment i on i.project_id = p.id where i.order_status = 1
and i.user_id = #{userId}
order by i.create_date desc
limit #{limitStart},#{limitEnd}

select plp.id, plp.ear_number, plp.create_user, plp.create_date, plp.is_read, u.path from project_life_picture plp inner join upload u on u.id = plp.upload_id where plp.status = 1
and plp.create_user = #{userId}
and plp.ear_number = #{earNum}
order by plp.create_date desc
limit #{limitStart},#{limitEnd}

  		select count(*)
		from project_life_picture plp
		inner join upload u on u.id = plp.upload_id
		where plp.status = 1

  		and plp.create_user = #{userId}


  		and plp.ear_number = #{earNum}

select 	p.*
	,u.path little_image 
from(
	SELECT * 
	FROM( 
		SELECT project.*, NULL new_rank, NULL tmp 
		FROM project 





				where true


		AND noob = 1 
		 ORDER BY ${orderByClause}  
		LIMIT ${limitEnd}
	) t_is_noob 

	UNION

	SELECT	* 
	FROM( 
		SELECT project.*, 
			IF ( @tmp = limit_days, @rank := @rank + 1, @rank := 1 ) AS new_rank, 
			@tmp := limit_days AS tmp
		FROM project 
		INNER JOIN ( SELECT @tmp := '', @rank := 1 ) t_rank_tmp ON TRUE





				where true


		AND noob = 0 
		ORDER BY limit_days ASC  ,${orderByClause}  
	) tmp_is_not_noob 
	WHERE new_rank <= ${limitEnd}

)p
INNER JOIN ( SELECT MAX( project_id ) project_id, id, upload_id FROM project_picture WHERE `status` = 0 AND `type` = 1 GROUP BY project_id ) pp ON p.id = pp.project_id
INNER JOIN upload u ON pp.upload_id = u.id
