mapper.select:
select u.id,u.username,u.true_name trueName,u.phone,u.sex,u.birthday,u.email, u.create_date,u.identity_card certNo, u.register_ip registerIp,u.status from user u,assets a where u.id=a.user_id
and (username like '%${keyword}%' or true_name like '%${keyword}%' or phone like '%${keyword}%')
and status=#{status}
and (true_name is null and identity_card is null)
and true_name is not null and identity_card is not null
limit #{start},#{limit}

SELECT *
FROM user u, assets a
WHERE u.id = a.user_id AND register_date BETWEEN #{startTime} AND #{endTime}

SELECT *
FROM user u, assets a
WHERE u.id = a.user_id AND a.huo_income > 0  AND u.status = 0;

SELECT u.*
FROM user u, assets a
WHERE u.id = a.user_id AND a.total_income > 0 AND u.status = 0;

SELECT u.id, u.true_name trueName, u.phone, wb.lock_time lockTime, wb.reason, u.create_date, a.balance_amount FROM withdraw_black wb LEFT JOIN user u ON u.id = wb.user_id LEFT JOIN assets a ON wb.user_id = a.user_id
where (u.true_name like '%${likeSearch}%' or u.phone like '%${likeSearch}%')
ORDER BY wb.lock_time DESC limit ${page},${limit}

select u.id,u.username,u.true_name trueName,u.phone,u.sex,u.birthday,u.email, u.create_date,u.identity_card certNo, u.register_ip registerIp from user u where u.status=0
and (username like '%${keyword}%' or true_name like '%${keyword}%' or phone like '%${keyword}%')
and u.id not in (select e.user_id from withdraw_black e) limit #{start},#{limit}

select c.*,sum(g.creditorSurplusAmount) creditorSurplusAmount from ( SELECT b.*,sum( IF ( p.project_type = 4 AND p.status = 0, p.total_amount, 0 ) ) creditorAmount FROM ( SELECT u.id, u.username, u.phone, u.true_name trueName FROM user u LEFT JOIN assets a ON u.id = a.user_id WHERE u.status = 4
and (username like '%${keyword}%' or true_name like '%${keyword}%' or phone like '%${keyword}%')
) b, project p WHERE b.id = p.user_id or p.user_id is null GROUP BY b.id )c,( select (IFNULL((f.total_amount-f.invested_amount),0)-if(e.status=2,sum( e.total_amount - e.invested_amount),0)) creditorSurplusAmount,f.user_id from project f left join project_link_project l on f.id=l.parent_id left join project e on l.subcalss_id = e.id where f.project_type=4 group by f.id) g where c.id=g.user_id order by creditorSurplusAmount desc
where creditorSurplusAmount BETWEEN #{creditorStart} and #{creditorEnd}
limit #{start},#{limit}

select count(c.id) from ( SELECT b.*,sum( IF ( p.project_type = 4 AND p.status = 0, p.total_amount, 0 ) ) creditorAmount FROM ( SELECT u.id, u.username, u.phone, u.true_name trueName FROM user u LEFT JOIN assets a ON u.id = a.user_id WHERE u.status = 4
and (username like '%${keyword}%' or true_name like '%${keyword}%' or phone like '%${keyword}%')
) b, project p WHERE b.id = p.user_id or p.user_id is null GROUP BY b.id )c,( select (IFNULL((f.total_amount-f.invested_amount),0)-if(e.status=2,sum( e.total_amount - e.invested_amount),0)) creditorSurplusAmount,f.user_id from project f left join project_link_project l on f.id=l.parent_id left join project e on l.subcalss_id = e.id where f.project_type=4 group by f.id) g where c.id=g.user_id order by creditorSurplusAmount desc
where creditorSurplusAmount BETWEEN #{creditorStart} and #{creditorEnd}

