mapper.select:
select u.id userId, username, true_name trueName, u.phone phone, identity_card identityCard, bc.card_number cardNumber, bc.phone bankPhone, e.id enterpriseId from user u left join bank_card bc on u.id = bc.user_id left join enterprise e on u.id = e.user_id where true
and ( u.username like CONCAT('%',#{keyword},'%') or u.phone like CONCAT('%',#{keyword},'%') or u.true_name like CONCAT('%',#{keyword},'%') )
limit #{limit}, #{offset}

SELECT u.LEVEL FROM `user` u INNER JOIN assets a on u.id=a.user_id LEFT JOIN user_invite ui on u.id=ui.invite_user_id WHERE 1 = 1
and u.id=${userId}
limit 1

SELECT u.id, u.phone, u.true_name, u.identity_card, u.sex, u.is_migration, u.migration_time, IFNULL(tr.balance_amount, 0) migration_balance_amount, IFNULL(investment.count_investment, 0) count_investment, IFNULL(investment.sum_investment_amount, 0) sum_investment_amount, IFNULL(receive.sum_receive_corpus, 0) sum_receive_corpus, IFNULL(receive.sum_receive_interest, 0) sum_receive_interest FROM (SELECT * FROM `user` WHERE `user`.is_migration
and (`user`.true_name like '%${keyWord}%' or `user`.phone like '%${keyWord}%' or `user`.identity_card like '%${keyWord}%')
and migration_time >= #{mirgationStartTime}
and migration_time < #{mirgationEndTime}
limit ${start} , ${limit}
) u LEFT JOIN trade_record tr ON tr.user_id = u.id AND aoe_type = 'migration_balance_add' LEFT JOIN (SELECT user_id, COUNT(1) count_investment, SUM(amount) sum_investment_amount FROM migration_investment GROUP BY user_id) investment ON investment.user_id = u.id LEFT JOIN (SELECT user_id, SUM(receive_corpus) sum_receive_corpus, SUM( receive_interest + receive_increase_interest ) sum_receive_interest FROM migration_investment_bill WHERE ! is_receive_before GROUP BY user_id) receive ON receive.user_id = u.id

SELECT count(1) FROM (SELECT * FROM `user` WHERE `user`.is_migration
and (`user`.true_name like '%${keyWord}%' or `user`.phone like '%${keyWord}%' or `user`.identity_card like '%${keyWord}%')
and migration_time >= #{mirgationStartTime}
and migration_time < #{mirgationEndTime}
) u LEFT JOIN trade_record tr ON tr.user_id = u.id AND aoe_type = 'migration_balance_add' LEFT JOIN (SELECT user_id, COUNT(1) count_investment, SUM(amount) sum_investment_amount FROM migration_investment GROUP BY user_id) investment ON investment.user_id = u.id LEFT JOIN (SELECT user_id, SUM(receive_corpus) sum_receive_corpus, SUM( receive_interest + receive_increase_interest ) sum_receive_interest FROM migration_investment_bill WHERE ! is_receive_before GROUP BY user_id) receive ON receive.user_id = u.id
